def factorial(n):
   if n == 0:
       return 1
   return n * factorial(n-1)

def sqrt(n):
   low = 0
   high = n

   while low <= high:
       mid = (low + high) // 2
       if mid * mid == n:
           return mid
       elif mid * mid < n:
           low = mid + 1
       else:
           high = mid - 1

   return low - 1

